CREATE TABLE blocks  (
  ID       	bigint(20)  NOT NULL  AUTO_INCREMENT COMMENT 'Идентификатор',
  NAMEBLOCK	varchar(250) NULL COMMENT 'Имя блока',
  DESCR    	varchar(4000) NULL COMMENT 'Описание',
  BLNUM    	varchar(50) NULL COMMENT 'номер',
  PRIMARY KEY (ID)
)  ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='блоки MARС формата';


CREATE TABLE format_fields  (
  ID       	bigint(20)  NOT NULL  AUTO_INCREMENT COMMENT 'Идентификатор',
  MFORMAT	varchar(50) NULL COMMENT 'синтаксис',
  FIELD_NAME	varchar(50) NULL COMMENT 'Имя поля',
  BLOCK_ID    bigint(20) NULL COMMENT 'блок',
  DESCR    	varchar(4000) NULL COMMENT 'описание',
  IS_SUB_FIELD  char(1) NULL COMMENT 'подполе',
  IS_MANDATORY    	char(1) NULL COMMENT 'обязательность',
  IS_REPEATABLE    	char(1) NULL COMMENT 'списочное',
  FIELD_COMMENT    	varchar(2000) NULL COMMENT 'комментарий',
  PARENT_FIELD_ID   bigint(20) NULL COMMENT 'родительское поле',
  NOTES    	varchar(4000) NULL COMMENT 'комментарий',
  PRIMARY KEY (ID)
)  ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='поля MARС формата';


