INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2292', 'RUSMARC', '001', 30, N'Поле содержит набор символов, однозначно идентифицирующий запись, а именно контрольный номер записи, присвоенный агентством, её подготовившим.
    <p /><b>Наличие</b><p />Обязательное.<br />
        Не повторяется.
    <p /><b>Индикаторы</b><p />В соответствии с ISO 2709 это поле не имеет индикаторов.
    <p /><b>Подполя</b><p />В соответствии с ISO 2709 это поле не содержит подполей.
    ', 0, 1, 0, N'Идентификатор записи', NULL, N'Никаких ограничений на форму идентификатора записи не устанавливается. Идентификатором записи может выступать, например, любой международный стандартный номер.
<P />Когда идентификатором записи выступает любой международный стандартный номер, номер национального уровня или номер, включающий в себя эти номера и другие данные, такая информация должна записываться и в предназначенные для нее поля. ');
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2293', 'RUSMARC', '003', 30, N'Поле содержит постоянный идентификатор записи, присвоенный агентством, подготовившим, использующим или распространяющим запись. Идентификатор, записываемый в поле, является постоянным идентификатором библиографической записи, а не ресурса, в ней описываемого.
    <p /><b>Наличие</b><p />Факультативное.<br />
        Не повторяется.
    <p /><b>Индикаторы</b><p />В соответствии с ISO 2709 это поле не имеет индикаторов.
    <p /><b>Подполя</b><p />В соответствии с ISO 2709 это поле не содержит подполей.

    ', 0, 0, 0, N'Постоянный идентификатор записи', NULL, N'<P />Постоянный идентификатор – это веб-адрес, который позволяет включать ссылку на электронный документ, будучи при этом уверенным, что эта ссылка не изменится. Существуют ряд систем, предусматривающих создание постоянных идентификаторов.
      <P />Библиографическая запись, хранящаяся в депозитарии OAI, также может иметь постоянный идентификатор. Благодаря этому инструменту ссылка на библиографическую запись может быть указана на веб-сайте, в блоге или на форуме в виде веб-адреса.
    ');
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2294', 'RUSMARC', '005', 30, N'Поле содержит дату и время последней обработки записи. Это позволяет автоматизированной системе определить, является ли версия обрабатываемой записи последней, предыдущей или той же, что была обработана ранее.
    <p /><b>Наличие</b><p />Факультативное, рекомендуется.<br />
        Не повторяется.
    <p /><b>Индикаторы</b><p />В соответствии с ISO 2709 это поле не имеет индикаторов.
    <p /><b>Подполя</b><p />В соответствии с ISO 2709 это поле не содержит подполей.

    ', 0, 0, 0, N'Идентификатор версии', NULL, N'Данные вводятся в формате стандарта ISO 8601-2004 для дат: ГГГГММДД, где ГГГГ представляет год, ММ - месяц, ДД - число месяца. Время вводится в форме ЧЧММСС.Т, где ЧЧ означает час, исходя из 24-часового отсчета, ММ - минуты, СС - секунды и Т - десятые доли секунды. Для всех чисел, если необходимо, добавляют начальные нули.');
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2295', 'RUSMARC', '010', 30, N'Поле содержит Международный стандартный номер книги и уточнения, которые определяют различия между номерами ISBN, если в записи содержится более одного номера. Поле (за исключением подполя $9) соответствует Области стандартного номера (или его альтернативы) и условий доступности стандартов ISBD и ГОСТ 7.1-2003. Подполе $9 относится к Области примечания ГОСТ 7.1-2003.', 0, 0, 1, N'Международный стандартный номер книги (ISBN)', NULL, N'<P><b>Структура ISBN</b></P><p>С 1.01.2007 все национальные агентства присваивают 13-значные ISBN, которые состоят из аббревиатуры «ISBN» и тринадцати цифр, разбитых на пять групп, которые отделяются друг от друга дефисом. Цифровая часть ISBN включает пять групп цифр:
      </p><P><li>префикс;</li><li>номер регистрационной группы;</li><li>номер регистранта, который присваивается  Национальным агентством ISBN издателю (производителю документов);</li><li>номер издания (публикации);</li><li>контрольная цифра.</li></P><P>Аббревиатура ISBN в поле не вводится и при выовде записи генерируется автоматически.
</P><P><b>Префикс</b> – трехзначный код, предоставленный Европейской ассоциацией товарной нумерации (EAN) Международному агентству ISBN для обозначения товара «Книжная продукция». В настоящее время предусмотрено применение кодов – 978 и 979.<br /><b>Номер регистрационной группы</b> идентифицирует в системе ISBN страну, географический регион или языковую область. Длина этого элемента – от 1 до 5 цифр. Для Российской Федерации номер регистрационной группы  – цифра 5.<br /><b>Номер регистранта</b> идентифицирует в системе ISBN конкретного издателя / производителя документов в прамках регистрационной группы. Длина этого варьируется для разных издателей, в зависимости от предполагаемого годового числа изданий в год и может иметь от 2 до 7 цифр.<br /><b>Номер издания (публикации)</b> идентифицирует конкретное издание (публикацию) издателя с указанным номером регистранта. Длина этого элемента также варьируется в зависимости предшествующих элементов – от числа изданий в данном издательстве и от номера регистранта. Для разных издательств номер издания может варьироваться от 2 до 6 цифр.<br /><b>Контрольная цифра</b> служит для проверки правильности указанного ISBN. Контрольная цифра рассчитывается по специальному алгоритму.
</P><P>10-значный ISBN, применявшийся ранее, новым изданиям не присваивается.<br />

Конвертер для перевода 10-значных номеров ISBN в 13-значные номера см.: <a target="_top" href="http://www.isbn-international.org/converter/converter.html">http://www.isbn-international.org/converter/converter.html</a>.
</P>');
