INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2301', 'RUSMARC', '011', 30, N'Поле содержит Международный стандартный номер сериального издания, присвоенный документу центром ISSN, любой ISSN, который был присвоен или аннулирован, а также сведения об условиях доступности и / или цене издания. Это поле (за исключением подполя $9) соответствует Области стандартного номера (или его альтернативы) и условий доступности стандартов ISBD(CR) и ГОСТ 7.1-2003. Подполе $9 относится к Области примечания ГОСТ 7.1-2003.', 0, 0, 1, N'Международный стандартный номер сериального издания (ISSN)', NULL, N'<p />Поле 011 может присутствовать только в записях на издание сериального уровня (содержащих код "s" в позиции маркера 7). В поле 011 вводится ISSN серии, заглавие которой приведено в поле 200.<br />
    Для вывода на карточку ISSN серии или подсерии в области серии используется подполе $x поля 225.<br />Если заглавию в поле 200 соответствует более одного правильного ISSN, то поле 011 повторяется для каждого ISSN (в том числе для указания ISSN издания, выходящего на разных носителях). Если поле 200 содержит заглавие серии, включающей собираемую подсерию, то ISSN подсерии вводится в запись на подсерию. Если серия включает несобираемую серию, то ISSN подсерии вводится в поле 011, встроенное в поле 462 УРОВЕНЬ ПОДНАБОРА. Если подсерия включается в несобираемую серию, то ISSN серии вводится в поле 011, встроенное в поле 461 УРОВЕНЬ НАБОРА.<BR /><b>ISSN-L:</b> В соответствии с ISO 3297:2007 (<i>Information and documentation – International Standard Serial number (ISSN)</i>) ISSN-L используется для группировки, или связи версий одного продолжающегося ресурса, выходящего на разных носителях (например, печатной и электронной версий журнала).');
