INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2527', 'RUSMARC', '146b', 31, N'Каждое вхождение подполя содержит девятисимвольный код, обозначающий тип голоса или инструмента, количество голосов или инструментов указанного типа, а также другую релевантную информацию (примеры <a href="examp1.htm#p1462">2-3</a>, <a href="examp1.htm#p1465">5-7</a>, <a href="examp1.htm#p14618">18-20</a>).<br />
      Позиции символов 0-1 содержат двузначное число, обозначающее количество солистов для каждого типа голосов или инструментов, с добавлением начального нуля в случае необходимости. Если количество солистов не определено, указывается значение «uu».<br />
      Позиции 2-4 содержат код типа средства исполнения (голоса или инструмента). Используются коды, приведенные в списке кодов <a href="codes_146.htm#a1">A/1-9</a>, <a href="codes_146.htm#a13">A/13</a>.
      Позиции 5-8 содержат другую необходимую информацию (тесситура и т.д.). Используются коды, приведенные в списках кодов <a href="codes_146.htm#b">B-C</a>.
      ', 1, 0, 1, N'Инструмент / голос - соло ', 2525, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2528', 'RUSMARC', '146c', 31, N'Каждое вхождение подполя содержит девятисимвольный код, обозначающий тип голоса или инструмента, общее количество голосов или инструментов указанного типа, и другую релевантную информацию (примеры <a href="examp1.htm#p146">1-4</a>, <a href="examp1.htm#p14610">10-13</a>, <a href="examp1.htm#p14615">15-17</a>, <a href="examp1.htm#p14619">19-20</a>, <a href="examp1.htm#p14622">22</a>).<br />
      Позиции символов 0-1 содержат двузначное число, обозначающее количество голосов, инструментов и т.п. каждого типа, с добавлением начального нуля в случае необходимости. Если это количество не определено, указывается значение «uu».<br />
      Позиции 2-4 содержат код типа средства исполнения (голоса, инструмента и т.п.). Используются коды, приведенные в списке кодов <a href="codes_146.htm#a1">A/1-9</a>), <a href="codes_146.htm#a12">A/2-13</a>).<br />
      Позиции 5-8 содержат другую необходимую информацию (тесситура и т.д.). Используются коды, приведенные в списках кодов <a href="codes_146.htm#b">B-C</a>.<br />
