INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2655', 'RUSMARC', '320u', 33, N'
      Универсальный идентификатор ресурса (URI), например, URL или URN, который представляет собой данные для электронного доступа в стандартном синтаксисе. Эти данные могут использоваться для автоматического доступа к электронному ресурсу через один из протоколов сети INTERNET. Подполе $u может использоваться в случае, если имеется электронная версия библиографии / указателя (например, оцифрованный фрагмент документа или часть электронного документа).
      ', 1, 0, 1, N'Универсальный идентификатор ресурса (URI)', 2653, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2656', 'RUSMARC', '321', 33, N'Поле содержит примечание, указывающее на отражение в библиографических и реферативных указателях ссылок на обрабатываемый документ. В частности, данное поле может содержать краткие ссылки на библиографию, каталог и т. д., где данный документ упоминается или описывается.
', 0, 0, 1, N'Примечания об отдельно изданных указателях / рефератах / ссылках, отражающих каталогизируемый документ', NULL, N'Примечания в данном поле могут быть структурированы или записываться в свободной форме.');
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2657', 'RUSMARC', '321a', 33, N'Наименование внешнего источника, в котором цитируется документ. Это может быть либо название источника, либо наименование организации. По возможности рекомендуется использовать ключевое заглавие.
', 1, 1, 0, N'Примечания об указателях, рефератах, ссылках', 2656, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
