INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2661', 'RUSMARC', '321x', 33, N'ISBN, ISSN либо другой международный стандартный номер внешнего источника, в котором отражен документ. Любой номер, кроме ISSN, должен указываться с соответствующим идентификатором (например, ISBN).
', 1, 0, 0, N'Международный стандартный номер', 2656, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2662', 'RUSMARC', '3215', 33, N'Наименование (в кодированной или полной форме) организации - держателя экземпляра, к которому относится поле. Коды должны вводиться в соответствии со стандартом <i>International Standard Identifier for Libraries and Related Organisations</i> (ISIL), ISO 15511 (<i>Международный стандартный идентификатор для библиотек и других связанных организаций</i>). (Ранее созданные записи могут содержать коды из списка <a href="http://www.loc.gov/marc/organizations" target="_top"><i>MARC Code List for Organizations</i></a>, полное наименование организации или национальный код). Если организация владеет более чем одним экземпляром, подполе также должно содержать шифр экземпляра, отделенный от наименования организации двоеточием.
', 1, 0, 0, N'Организация и экземпляр, к которому относится поле', 2656, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2663', 'RUSMARC', '3216', 33, N'Подполе содержит информацию, позволяющую при обработке данных связать поле с другими полями в записи. <br />
Порядок применения подполя $6 описан в Разделе 3. 13 Российского коммуникативного формата.
', 1, 0, 0, N'Связь между полями', 2656, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2664', 'RUSMARC', '322', 33, N'Поле содержит имена лиц и наименования организаций, которые принимали участие в художественном или техническом процессе создания документа (кроме главных исполнителей).
', 0, 0, 0, N'Примечания об участниках создания (визуально-проекционные и видеоматериалы, звукозаписи)', NULL, N'Термин "Credits" [участники] или его эквиваленты не должен вводиться в запись, т.к. он добавляется при выводе примечания автоматически.<br />
Если исходный формат не предусматривает различия между непосредственными исполнителями и участниками, как это предусмотрено в примечаниях UNIMARC, для обеих категорий участников создания документа должно использоваться поле 322 ПРИМЕЧАНИЕ ОБ УЧАСТНИКАХ СОЗДАНИЯ данное поле.');
