INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('2670', 'RUSMARC', '325', 33, N'Это поле содержит примечание, указывающее на то, что ЛИБО запись создается на основе копии документа, описываемого в записи, ЛИБО существует копия документа, описываемого в данной записи. Остальные элементы записи описывают оригинал.', 0, 0, 1, N'Примечание о копии', NULL, N'Поле используется в случае, если (а) в соответствии с практикой каталогизирующего учреждения документ, являющийся копией, описывается в блоке 2-- как оригинал, а подробная информация о репродукции приводится в примечании, либо (б) каталогизирующее учреждение использует примечание для того, чтобы отразить информацию о существовании копии документа, описываемого в записи. В обоих случаях блок 2-- содержит описание оригинала. Текст примечания должен обеспечить четкое понимание назначения примечания. Рекомендуется приводить любую ссылку на оригинал с использованием пунктуации ISBD.');
