INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('3201', 'RUSMARC', '7104', 37, N'
	Код, используемый для указания взаимосвязи между организацией, указанной в поле, и каталогизируемым документом. Коды отношений могут быть цифровыми или буквенно-цифровыми. Список цифровых кодов см. <a href="supc.htm#sc">Приложение С</a>. Если необходимо дать более точную информацию о роли исполнителей музыкального произведения, дополнительно могут использоваться бкувенные коды. В этом случае указывается сначала цифровой код, и далее, в повторяющемся подполе $4, – соответствующий буквенный код.<br />Перечни буквенных кодов регулярно обновляются IAML и размещены на сайте IAML (<a href="http://www.iaml.info/activities/cataloguing/unimarc/medium">http://www.iaml.info/activities/cataloguing/unimarc/medium</a>).
	', 1, 0, 1, N'Код отношения', 3189, NULL);
INSERT INTO format_fields(ID, MFORMAT, FIELD_NAME, BLOCK_ID, DESCR, IS_SUB_FIELD, IS_MANDATORY, IS_REPEATABLE, FIELD_COMMENT, PARENT_FIELD_ID, NOTES)
VALUES('3202', 'RUSMARC', '711', 37, N'
Поле содержит наименование организации, для которой определен статус альтернативной ответственности по отношению к каталогизируемому документу; наименование организации представлено в форме точки доступа.<br />
Если основная точка доступа в библиографической записи создается на наименование организации, то статус альтернативной ответственности определяется для второй и последующих организаций, являющихся авторами каталогизируемого документа, наименования которых используются как точки доступа. Например: совместное издание двух организаций (при составлении библиографической записи под наименованием одной из них); выставка 2-х или более художников.<br />
Если основная точка доступа в библиографической записи создается на заглавие, то статус альтернативной ответственности определяется для всех организаций - авторов каталогизируемого документа, наименования которых используются как точки доступа.<br />
Если основная точка доступа в библиографической записи создается на имя лица, то все организации, несущие ответственность, для которых требуется создание точек доступа, записываются в поле 712.
', 0, 0, 1, N'Наименование организации - альтернативная ответственность', NULL, N'Применяются те же правила, что для поля 710. ');
